
SELECT * from blogger where created_at > '2020-01-01 00:00:00.00';
UPDATE blogger set password='bad_password' where password='pass';
SELECT post.content, post.title, blogger.username FROM post INNER JOIN blogger ON post.blogger_id = blogger.id;
SELECT comment.content, post.title, blogger.username from comment INNER JOIN post ON comment.post_id = post.id INNER JOIN blogger ON comment.blogger_id = blogger.id;
DELETE FROM comment WHERE content LIKE '%delete me%';
SELECT post.id, post.content, post.title, blogger.username FROM post INNER JOIN blogger ON post.blogger_id = blogger.id WHERE post.id = (select post_id from comment GROUP BY post_id HAVING COUNT(*) >= 3);
